    }
  }
];

let kb = [];
const payload = $input.first()?.json;
if (Array.isArray(payload)) {
  kb = payload;
} else if (Array.isArray(payload?.kb)) {
  kb = payload.kb;
} else if (Array.isArray(payload?.data)) {
  kb = payload.data;
} else {
  kb = [
    { id: "KB001", title: "Password Reset", content: "To reset your account password, click 'Forgot Password' on the login page, enter your registered email address, and follow the instructions in the reset email." },
    { id: "KB002", title: "Change Billing Address", content: "Log in to your account, go to 'Account Settings' > 'Billing', and click 'Edit Billing Address'. Save changes before exiting." },
    { id: "KB003", title: "Cancel Subscription", content: "To cancel your subscription, go to 'Account Settings' > 'Subscription', select 'Cancel Subscription', and confirm the cancellation. Your subscription will remain active until the end of the billing cycle." },
    { id: "KB004", title: "Refund Policy", content: "We offer a 30-day money-back guarantee on all annual subscriptions. Refunds are processed within 5-7 business days after approval." },
    { id: "KB005", title: "Two-Factor Authentication", content: "Enable 2FA by navigating to 'Account Settings' > 'Security', clicking 'Enable Two-Factor Authentication', and scanning the QR code with an authenticator app." }
  ];
}
if (!Array.isArray(kb) || kb.length === 0) {
  throw new Error("Knowledge base is empty or failed to load.");
}
const seen = new Set();
kb = kb
  .filter((r) => r && r.id && r.content)
  .filter((r) => { const k = String(r.id).trim(); if (seen.has(k)) return false; seen.add(k); return true; })
  .map((r) => ({ id: String(r.id).trim(), title: String(r.title || "").trim(), content: String(r.content || "").trim() }));
return [{ json: { kb } }];

const inItem = $input.first();
if (!inItem) throw new Error("No input for BM25 Rank.");
const { question, kb, mode } = inItem.json;
function tokenize(text) {
  return text.toLowerCase().replace(/[^a-z0-9\s>\/']/gi, " ").split(/\s+/).filter(Boolean).filter((t) => !STOP.has(t));
}
const STOP = new Set(["a","an","the","and","or","but","if","then","than","so","to","of","in","on","for","with","as","by","is","are","was","were","be","been","at","it","this","that","these","those","we","you","your","our","i"]);
const k1 = 1.5, b = 0.75;
const docs = kb.map((d) => ({ id: d.id, title: d.title || "", content: d.content || "", tokens: tokenize(`${d.title || ""} ${d.content || ""}`) }));
const N = docs.length;
const docLens = docs.map((d) => d.tokens.length);
const avgdl = docLens.reduce((a,b)=>a+b,0)/Math.max(N,1);
const df = new Map();
for (const d of docs){ const uniq=new Set(d.tokens); for (const t of uniq) df.set(t,(df.get(t)||0)+1); }
const idf = new Map();
for (const [t,n_i] of df.entries()){ idf.set(t, Math.log((N - n_i + 0.5) / (n_i + 0.5) + 1)); }
for (const d of docs){ d.tf = new Map(); for (const t of d.tokens) d.tf.set(t,(d.tf.get(t)||0)+1); }
const qTokens = tokenize(question || "");
const scored = docs.map((d, idx) => { let s = 0; for (const t of qTokens){ const f=d.tf.get(t)||0; if(!f) continue; const idf_t=idf.get(t)||0; const denom = f + k1 * (1 - b + (b * docLens[idx]) / avgdl); s += idf_t * ((f * (k1 + 1)) / Math.max(denom, 1e-9)); } return { d, score: s }; });
const max = Math.max(...scored.map(x=>x.score),0), min = Math.min(...scored.map(x=>x.score),0);
const normed = scored.map(x=>({ ...x, norm: max===min?0:(x.score-min)/(max-min) })).sort((a,b)=>b.norm-a.norm);
const top_k = Math.min(3, Math.max(0, kb.length));
const selected = normed.slice(0, top_k).filter(x=>x.norm>0);
return [{ json: { question, mode: mode || "bm25", retrieval: { method: "bm25", top_k, selected: selected.map(x=>({ id:x.d.id, title:x.d.title, content:x.d.content, score:x.norm })), retrieval_conf: selected.length ? selected[0].norm : 0 } } }];

const inItem = $input.first();
if (!inItem) throw new Error("No input for Prepare Embeddings.");
const { kb, question, mode } = inItem.json;
return [{ json: { question, mode: "emb", texts: kb.map(d=>`${d.title || ""}\n\n${d.content || ""}`), meta: kb.map(d=>({ id: d.id, title: d.title || "" })) } }];

const j = $input.first().json;
const qEmb = Array.isArray(j.data) && j.data[0] && Array.isArray(j.data[0].embedding) ? j.data[0].embedding : [];
return [{ json: { query_embedding: qEmb } }];

function cosine(a,b){ let dot=0,na=0,nb=0; const len=Math.min(a.length,b.length); for(let i=0;i<len;i++){const x=a[i],y=b[i]; dot+=x*y; na+=x*x; nb+=y*y;} if(!na||!nb) return 0; return dot/(Math.sqrt(na)*Math.sqrt(nb)); }
const j=$input.first().json;
const docEmb=j.doc_embeddings||[];
const qEmb=j.query_embedding||[];
const texts=j.texts||[];
const meta=j.meta||[];
const question=j.question;
const mode=j.mode||"emb";
if(!docEmb.length||!qEmb.length){ return [{ json:{ question, mode, retrieval:{ method:"emb", top_k:0, selected:[], retrieval_conf:0 } } }]; }
const scores=docEmb.map((e,i)=>({ idx:i, score:cosine(e,qEmb) }));
const maxS=Math.max(...scores.map(s=>s.score),0), minS=Math.min(...scores.map(s=>s.score),0);
const norm=scores.map(s=>({ ...s, norm:maxS===minS?0:(s.score-minS)/(maxS-minS) })).sort((a,b)=>b.norm-a.norm);
const top_k=Math.min(3, texts.length);
const selected=norm.slice(0, top_k);
const selectedDocs=selected.map(s=>({ id: meta[s.idx]?.id ?? String(s.idx), title: meta[s.idx]?.title ?? "", content: texts[s.idx], score: s.norm }));
return [{ json: { question, mode, retrieval: { method:"emb", top_k, selected: selectedDocs, retrieval_conf: selected.length ? selected[0].norm : 0 } } }];

const j=$input.first().json;
const question=j.question||"";
const retrieval=j.retrieval||{ selected:[], retrieval_conf:0 };
const contexts=(retrieval.selected||[]).map((d,i)=>{ const safe=(s)=>String(s??"").replace(/"/g,'\\"'); return `KB[${i+1}]: { "id": "${safe(d.id)}", "title": "${safe(d.title)}", "content": "${safe(d.content)}" }`; }).join("\n\n");
const schema={ type:"object", required:["answer","confidence_score","sources"], properties:{ answer:{type:"string"}, confidence_score:{type:"number",minimum:0,maximum:1}, sources:{type:"array",items:{type:"string"},minItems:0} } };
const system=(
`You are a Customer Support Knowledge Agent.
Rules:
- Only answer using the provided KB contexts.
- If contexts are insufficient, say you don't have enough info and suggest next steps. Do not invent facts.
- Be concise and task-oriented for customers.
- Output must be a single JSON object and valid JSON, no extra text.
- Follow this JSON schema exactly (types and keys):
${JSON.stringify(schema)}
`).trim();
const user=(
`User question:
"${question}"

Relevant contexts:
${contexts}

Instructions:
1) Use only these contexts to answer.
2) List sources as an array of KB ids (e.g., ["KB001"]).
3) Set confidence_score between 0 and 1.
4) If you cannot answer from the contexts, return a helpful message and set confidence_score <= 0.4.

Return only JSON.
`).trim();
return [{ json: { messages:[ {role:"system", content:system}, {role:"user", content:user} ], question, mode:j.mode||"bm25", retrieval, retrieval_conf: retrieval.retrieval_conf||0 } }];

function clamp(x,lo,hi){ return Math.max(lo, Math.min(hi,x)); }
const j=$input.first().json;
const retrieval_conf = Number(j.retrieval_conf ?? 0) || 0;
const question = j.question;
const mode = j.mode || "bm25";
const retrieval = j.retrieval || { selected: [] };
const raw = j?.choices?.[0]?.message?.content;
if (!raw || typeof raw !== "string") {
  return [{ json: { error:true, message:"LLM call failed or returned no content", details:j, question, mode } }];
}
let parsed;
try { parsed = JSON.parse(raw); } catch {
  const m = raw.match(/\{[\s\S]*\}/);
  if (m) { try { parsed = JSON.parse(m[0]); } catch { return [{ json: { error:true, message:"LLM returned invalid JSON and repair failed.", details:{ raw }, question, mode } }]; } }
  else { return [{ json: { error:true, message:"LLM returned invalid JSON.", details:{ raw }, question, mode } }]; }
}
if (typeof parsed.answer !== "string") return [{ json: { error:true, message:"Invalid schema: 'answer' must be string.", details:parsed, question, mode } }];
if (typeof parsed.confidence_score !== "number" || Number.isNaN(parsed.confidence_score)) return [{ json: { error:true, message:"Invalid schema: 'confidence_score' must be number.", details:parsed, question, mode } }];
if (!Array.isArray(parsed.sources)) return [{ json: { error:true, message:"Invalid schema: 'sources' must be array.", details:parsed, question, mode } }];
const blended = 0.6*clamp(parsed.confidence_score,0,1) + 0.4*clamp(retrieval_conf,0,1);
const allowedIds = new Set((retrieval.selected||[]).map(s=>String(s.id).trim()));
let sources = Array.from(new Set((parsed.sources||[]).map(s=>String(s).trim()).filter(Boolean))).filter(s=>allowedIds.has(s));
if (sources.length===0 && (retrieval.selected||[]).length>0) sources=[String(retrieval.selected[0].id)];
return [{ json: { error:false, result:{ answer: parsed.answer.trim(), confidence_score: clamp(Number(blended.toFixed(3)),0,1), sources }, question, mode, retrieval_method: retrieval.method || null } }];
